import React from 'react';
import api from '../utils/api';

class UpCommingMatches extends React.Component {
  state={
    matches:[]
  }
  componentDidMount(){
 this.renderUpcomingMatches()
  }
  renderUpcomingMatches=()=>{
    api.get('/api/matches')
    .then((response) => {
      this.setState({matches:response.data.matches})
    })
    .catch((error) => {
      console.log(error)
    })
  }
  render(){
    let { matches } = this.state;
    return (
      <div className="card">
        <div className="card-header">
          Up Coming Fixures
        </div>
        <ul className="list-group list-group-flush">
          {matches.map((item,index)=>{
            return(
              <li className="list-group-item" key ={item.unique_id}>
              <span class="team1">{item['team-1']}</span>
              <span class="vs">Vs</span>
              <span class="team2">{item['team-2']}</span>
            </li>
            )

          })}
        </ul>
      </div>
    )
  }

}
export default UpCommingMatches;
