import React from 'react';
import api from '../utils/api'

class PlayerDetails extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      playerDetails:{},
      players:[]
    }
  }
  handleSearchPlayer=()=>{
    let playerName = this.refs.name.value;
    api.get(`/api/playerFinder?name=${playerName}`)
    .then((response) => {
      this.setState({players:response.data})
      console.log(response)
    })
    .catch((error) => {
      this.setState({players:[]})
    })
  }

  render(){
    let players =  this.state.players
    return (
      <div>
      <div className="input-group mb-3">
        <input type="text" ref="name" className="form-control" placeholder="search player"/>
        <div className="input-group-append">
          <button class="btn btn-danger" onClick={this.handleSearchPlayer} type="button">Search</button>
        </div>
      </div>
      {players && players.length>0?
        players.map((item,index)=>{
          return(
            <button type="button" class="btn btn-primary">{players.name}</button>
          )
        }):''}
      </div>
    )
  }

}
export default PlayerDetails;
