import React from 'react';

class PlayerDetails extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      playerDetails:{},
      players:[]
    }
  }
  handleSearchPlayer=()=>{
    let playerName = this.refs.name.value;
  }

  renderPlayers=()=>{
    api.get(`/api/playerFinder?name=${playerName}`)
    .then((response) => {
      this.setState({players:response.data.players})
    })
    .catch((error) => {
      this.setState({matches:[]})
    })
  }

  render(){
    let { playerDetails } = this.props;
    return (
      <div className="input-group mb-3">
        <input type="text" ref="name" className="form-control" placeholder="search player"/>
        <div className="input-group-append">
          <button class="btn btn-danger" onClick={this.handleSearchPlayer} type="button">Search</button>
        </div>
      </div>
    )
  }

}
export default PlayerDetails;
