import React from 'react';
import MainComponent from '../assets/css/MainComponent.style';
import RecentMatches from '../components/UpCommingMatches';
import MatchDetails from '../components/MatchDetails';
import api from '../utils/api'

class Main extends React.Component {
  state={
    matches:[],
    matchDetails:{},
  }
  componentDidMount(){
    this.renderUpcomingMatches()
  }
  handleMatchDetails=(id)=>{
    let {matches} =  this.state.matches;
    let matchDetails = matches.filter((item)=>{details.unique_id==id})
    this.setState({matchDetails:matchDetails});
  }
  renderUpcomingMatches=()=>{
    api.get('/api/matches')
    .then((response) => {
      this.setState({matches:response.data.matches})
    })
    .catch((error) => {
      this.setState({matches:[]})
    })
  }

  render(){
    return (
      <MainComponent>
          <div className="hero-banner">
            <div class="container">
              <h2>If you Don’t Practice
                You <br/><span style={{color:'#ffcb05'}}>Don’t Derserve</span><br/>
                to win!
              </h2>
            </div>
          </div>
          <div class="container" style={{paddingTop:42,paddingBottom:42}}>
            <div class="row">
              <div class="col-md-4">
                <RecentMatches recentMatches = {this.state.matches}/>
              </div>
              <div class="col-md-8">
                <MatchDetails details = {this.state.matchDetails}/>
              </div>
            </div>
          </div>
      </MainComponent>
    )
  }

}
export default Main;
